#!/bin/sh
$HOME/buildroot/output/host/bin/qemu-system-arm -M vexpress-a9 -smp 2 -m 1024 -nographic -kernel $HOME/buildroot/output/images/zImage -dtb output/images/vexpress-v2p-ca9.dtb -drive file=$HOME/buildroot/output/images/rootfs.ext2,if=sd,format=raw -append "console=ttyAMA0,115200 rootwait root=/dev/mmcblk0" -net nic,model=lan9118 -net user,hostfwd=tcp::2222-:22 -fsdev local,id=v_9p_dev,path=$HOME/buildroot,security_model=none -device virtio-9p-device,fsdev=v_9p_dev,mount_tag=hostshare
