#!/bin/sh
$HOME/buildroot/output/host/bin/qemu-system-arm -M virt,highmem=off -smp 2 -m 2048 -nographic -kernel $HOME/buildroot/output/images/zImage -drive file=$HOME/buildroot/output/images/rootfs.ext2,if=none,format=raw,id=hd0 -device virtio-blk-device,drive=hd0 -append "console=ttyAMA0,115200 rootwait root=/dev/vda" -netdev user,id=eth0 -device virtio-net-device,netdev=eth0 -net user,hostfwd=tcp::2222-:22 -fsdev local,id=v_9p_dev,path=$HOME/buildroot,security_model=none -device virtio-9p-device,fsdev=v_9p_dev,mount_tag=hostshare "$@"
